<script src="/js/ckeditor/ckeditor.js" type="text/javascript" charset="utf-8" ></script><div class="container" style="z-index: 100; background-color: gray; position: absolute; left: 10%; top: 10%;">    <div class="row">        <div class="position-ref full-height">            <div class="content editor" style="text-align: center">                <form action=" {{ route('article.edit') }} " method="POST">                    {{ csrf_field() }}                    <input type="hidden" name="user_id" value="{{Auth::user()->id}}">                    <input type="hidden" name="documentationCategory_id" value="{{$id}}">                    <input type="hidden" name="article_id" value="{{$article->id}}">                    <label for="title">Заголовок</label><br />                    <input type="text" name="title" class="form-control" id="title" value="{{$article->title}}"> <br />                    <label for="text">Текст</label><br />                    <textarea name="text" id="chizi1">{{$article->text}}</textarea>                    <script>                        var editor = CKEDITOR.replace( 'chizi1',                            {                                filebrowserBrowseUrl: '/elfinder/ckeditor',                                language: 'en-us'                            }                        );                    </script>                    <button type="submit">Сохранить</button>                </form>            </div>        </div>    </div></div><script src="/js/ckeditor/ckeditor.js" type="text/javascript" charset="utf-8" ></script>